let online = function(){
    let events = this.events;
    if (events && events.status)
        return events.status.pld.online;
    return 0;
}

let cachePayload = function(payload) {
    let hd = payload.hd;
    if (hd.from.type == "dev") {
        let dysub = false;
        let dev = data.devices[hd.from.id];
        if (!dev) {
            dev = {};
            dysub = true;
        }
        dev.id = hd.from.id;
        dev.events = dev.events || {};
        if (hd.from.pid)
            dev.pid = hd.from.pid;
        dev.events[hd.entry.id] = payload;
        dev.online = online;
        data.devices[hd.from.id] = dev;
        if (dysub) node.send({device: dev}, null);
    }

}

common.cachePayload = cachePayload;

return [null, {payload: data}];

