let getHDTopic = function(hd) {
    let tpc = "";
    if (!hd.pref) hd.pref = {};
    if (!hd.pref.app) hd.pref.app = (ids.app ? ids.app : "0");
    if (!hd.pref.dom) hd.pref.dom = (ids.dom ? ids.dom : "0");

    if (!hd.from) hd.from = {};
    if (!hd.from.type) hd.from.type = "plf";
    if (!hd.from.id) hd.from.id = ids.plf;
    if (!hd.to) hd.to = {};
    if (!hd.to.type) hd.to.type = "0";
    if (!hd.to.id) hd.to.id = "0";
    if (!hd.entry) hd.entry = {};
    if (!hd.entry.type) hd.entry.type = "0";
    if (!hd.entry.id) hd.entry.id = "0";
    if (!hd.sid) hd.sid = "0";
    if (!hd.stp) hd.stp = 0;
    if (!hd.version) hd.version = global.get("version");

    tpc =   hd.pref.app + "/" + hd.pref.dom + "/" + 
            hd.from.type + "/" + hd.from.id + "/" + 
            hd.to.type + "/" + hd.to.id + "/" + 
            hd.entry.type + "/" + hd.entry.id;
    return tpc;
}


common.getHDTopic = getHDTopic;

return {payload: common};

