    Object.keys(objs).forEach(key => {
        let obj = objs[key];
        delete objs[key];
        key = key.replace(/\./g, "__");
        objs[key] = obj;
        if (typeof obj == "object" && recu) 
            this.keyDot2DUnder(obj, recu);
    })     
}

